import { toast } from 'react-toastify';

const hitToast = (variant, message) => {
    if (!['success'].includes(variant)) {
        throw new TypeError("The 'variant' parameter must be either 'success' or 'error'");
    }
    toast[`${variant}`](message, {
        position: "top-right",
        autoClose: 3000,
        hideProgressBar: false,
        closeOnClick: true,
        pauseOnHover: true,
        draggable: true,
        progress: undefined,
    });
}

export default hitToast;
